import * as Y from 'yjs'
import { QuillBinding } from 'y-quill'
import Quill from 'quill'
import QuillCursors from 'quill-cursors'
import { useCallback } from 'react'
import "./styles.css"

Quill.register('modules/cursors', QuillCursors);

function Editor(props) {
    console.log('in editor')
    console.log(props.events)
    //eventsource stuff in here


    new EventSource(`http://194.113.75.203:3001/connect/${text}`)

    events.onmessage = (event) => {
        if (event.type === 'sync') {
            console.log("in sync");
            //add data to y doc
        } else if (event.type === 'update') {
            //update y doc
        }
        console.log(event.data); //<- our data from the backend is here
        //now gotta figure out how to tell the typee of event (sync or update)
        //if sync add data to y doc obj
        //if update update crdt obj (which in this case would b y doc)

    };


    const wrapperRef = useCallback((wrapper) => {
        if (wrapper == null) return
        wrapper.innerHTML = ""
        const quill = new Quill(document.querySelector('#editor'), {
            modules: {
                cursors: true,
                toolbar: [
                    // adding some basic Quill content features
                    [{ header: [1, 2, false] }],
                    ['bold', 'italic', 'underline'],
                    ['image', 'code-block']
                ],
                history: {
                    // Local undo shouldn't undo changes
                    // from remote users
                    userOnly: true
                }
            },
            placeholder: 'Start collaborating...',
            theme: 'snow' // 'bubble' is also great
        })
        // A Yjs document holds the shared data
        const ydoc = new Y.Doc()
        // Define a shared text type on the document
        const ytext = ydoc.getText('quill')

        // Create an editor-binding which
        // "binds" the quill editor to a Y.Text type.
        const binding = new QuillBinding(ytext, quill)
    }, [])

    return <div id='editor' className='container' ref={wrapperRef}></div>
}

export default Editor;
