    const [editing, setEditing] = useState(false);
    const [text, setText] = useState("");

    const handleUpdate = (event) => {
        console.log(event.target.value);
        setText(event.target.value);
    }
    const handleSubmit = () => {
        console.log(`here: ${text}`);
        //EventSource stuff <- what we want 
        //ok the thin <- u little shit
        const events = new EventSource(`http://194.113.75.203:3001/connect/${text}`)
            //or we can just do addEventListener
        events.onmessage = (event) => {
               if(event.type === 'sync'){
                    console.log("in sync");
                    //add data to y doc
               }else if(event.type === 'update'){
                    //update y doc
               }
            console.log(event.data); //<- our data from the backend is here
            //now gotta figure out how to tell the typee of event (sync or update)
            //if sync add data to y doc obj
            //if update update crdt obj (which in this case would b y doc)

        };
        //fetch(`http://194.113.75.203:3001/connect/${text}`) //fetch isn't what we want here using this as a placeholder
        setEditing(true);
    }

    return <div>
       {editing ? 
       <Editor 
            event={events}
       /> : (<div>
                <input onChange={handleUpdate} type="text" placeholder="Enter doc id" name="id" id="id" required></input>
            <button onClick={handleSubmit} type="submit">Open</button> </div>)}
    </div>
}

export default Fetch;
